syntax = "proto3";
package pb.base;
option go_package = "server-9kg/server/pb/base";

// -----------------------------------------------
// [通用][結構定義]
message ListBool {
    repeated bool List      = 1 ;
}
message ListInt32 {
    repeated int32 List     = 1 ;
}
message ListInt64 {
    repeated int64 List     = 1 ;
}
message ListUInt32 {
    repeated uint32 List    = 1 ;
}
message ListUInt64 {
    repeated uint64 List    = 1 ;
}
message ListString {
    repeated string List    = 1 ;
}

// -----------------------------------------------
// [通用][錯誤碼定義]
enum EnumErrCode {
    NO_ERROR                            = 0 ;       // 無錯誤

    UNKNOWN_ERROR                       = 1 ;       // 未知錯誤
    PROGRAM_LOGIC_ERROR                 = 2 ;       // 程序邏輯錯誤

    SERVER_INTERNAL_ERROR               = 11 ;      // 伺服器內部錯誤
    SERVER_REQUEST_BUSY                 = 12 ;      // 伺服器請求忙碌
    SERVER_REQUEST_LIMIT_SPEED          = 13 ;      // 伺服器請求頻率上限(請稍後再試)
    SERVER_UNDER_MAINTAIN               = 14 ;      // 伺服器維護中

    WS_CONNECT_FAILED                   = 21 ;      // WebSocket連接失敗
    WS_CONNECT_ERROR                    = 22 ;      // WebSocket連接錯誤

    DB_CONNECT_FAILED                   = 31 ;      // 資料庫連線失敗
    DB_ACCESS_FAILED                    = 32 ;      // 資料庫存取失敗
    DB_DATA_NOT_FOUND                   = 33 ;      // 資料庫資料不存在

    REQUEST_ACTION_FAILED               = 41 ;      // 請求動作失敗
    REQUEST_PARAM_INVALID               = 42 ;      // 請求參數無效
    REQUEST_PARAM_IS_EMPTY              = 43 ;      // 請求參數為空
    REQUEST_PARAM_IS_ZERO               = 44 ;      // 請求參數為零
    REQUEST_PARAM_OVER_LIMIT            = 45 ;      // 請求參數超限
    REQUEST_PARAM_NOT_ENOUGH            = 46 ;      // 請求參數不足

    RESPONSE_PARAM_INVALID              = 51 ;      // 回應參數無效

    PROTO_FORMAT_ERROR                  = 61 ;      // 封包格式錯誤
    PROTO_PARAM_INVALID                 = 62 ;      // 封包參數無效
    PROTO_PARAM_IS_EMPTY                = 63 ;      // 封包參數為空
    PROTO_PARAM_IS_ZERO                 = 64 ;      // 封包參數為零
    PROTO_PARAM_OVER_LIMIT              = 65 ;      // 封包參數超限
    PROTO_PARAM_NOT_ENOUGH              = 66 ;      // 封包參數不足

    DATA_NULL                           = 81 ;      // 資料為空值
    DATA_IS_ZERO                        = 82 ;      // 資料為零
    DATA_NOT_EXIST                      = 83 ;      // 資料不存在
    DATA_IS_EXIST                       = 84 ;      // 資料已存在
    DATA_ON_DUPLICATED                  = 85 ;      // 資料重複
    DATA_INVALID                        = 86 ;      // 資料無效
    DATA_OVER_LIMIT                     = 87 ;      // 資料超限
    DATA_NOT_ENOUGH                     = 88 ;      // 資料不足
    DATA_FORMAT_ERROR                   = 91 ;      // 資料格式錯誤
    DATA_PARSE_FAILED                   = 92 ;      // 資料解析失敗
    DATA_ENCRYPT_FAILED                 = 93 ;      // 資料加密失敗
    DATA_DECRYPT_FAILED                 = 94 ;      // 資料解密失敗
    DATA_AUTHORIZE_FAILED               = 95 ;      // 資料授權失敗
    DATA_VERIFY_FAILED                  = 96 ;      // 資料驗證失敗
    DATA_SIGNATURE_ERROR                = 97 ;      // 資料簽名錯誤
    DATA_CHECKSUM_ERROR                 = 98 ;      // 資料校驗錯誤
    DATA_CALCULATE_FAILED               = 99 ;      // 資料計算失敗

    USER_IS_OFFLINE                     = 101 ;     // 用戶已離線
    USER_IS_DISCONN                     = 102 ;     // 用戶已斷線
    USER_IS_ONLINE                      = 103 ;     // 用戶已在線

    USER_IN_UNFINISHED_GAME_ROUND       = 111 ;     // 用戶在未結束遊戲局內
    USER_IS_DEALER                      = 112 ;     // 用戶坐莊中
    USER_IS_NOT_DEALER                  = 113 ;     // 用戶非莊家
    USER_IS_SEAT_PLAYER                 = 114 ;     // 用戶入座中
    USER_IS_NOT_SEAT_PLAYER             = 115 ;     // 用戶非入座玩家
    USER_IS_GUESTER                     = 116 ;     // 用戶為遊客
    USER_IS_NOT_GUESTER                 = 117 ;     // 用戶非遊客

    // 從[1001]開始 以下是給客戶端(Client)用的:
    GAME_TOKEN_INVALID                  = 1101;     // 遊戲驗證碼無效或已過期(請重新登入)
}

// -----------------------------------------------
// [通用][錯誤回應]
message ErrResp {
    EnumErrCode Code     = 1 ;   // 錯誤碼
    string Msg           = 2 ;   // 錯誤訊息
}
