syntax = "proto3";
package pb.client;
option go_package = "server-9kg/server/pb/client";

import "base/base.proto";
import "client/login/login.proto";
import "client/lobby/lobby.proto";
import "client/game/game0031.proto";

// -----------------------------------------------
// [客戶端]請求
message ClientRequest {
    oneof Request {
        pb.client.login.LoginAuthRequest                    LoginAuth                   = 11 ;  // 登入驗證請求
        pb.client.login.ReLoginAuthRequest                  ReLoginAuth                 = 12 ;  // 重登驗證請求
        pb.client.login.GetCurrGameInfoRequest              GetCurrGameInfo             = 13 ;  // 取得[當前遊戲資訊]請求

        pb.client.lobby.EnterLobbyRequest                   EnterLobby                  = 21 ;  // 入廳請求
        pb.client.lobby.ChangeLobbyRequest                  ChangeLobby                 = 22 ;  // 換廳請求
        pb.client.lobby.ChangeTableRequest                  ChangeTable                 = 23 ;  // 換桌請求
        pb.client.lobby.EnterSeatRequest                    EnterSeat                   = 24 ;  // 入座請求
        pb.client.lobby.LeaveSeatRequest                    LeaveSeat                   = 25 ;  // 離座請求

        pb.client.lobby.SeaterSendEmojiRequest              SeaterSendEmoji             = 31 ;  // 入座玩家發送表情請求

        pb.client.game.Game0031SeaterSendChatMsgRequest     Game0031SeaterSendChatMsg   = 3101; // [9KG-0031]入座玩家發送聊天訊息請求
        pb.client.game.Game0031PlayerBetRequest             Game0031PlayerBet           = 3111; // [9KG-0031]閒家押注請求
        pb.client.game.Game0031PlayerSideBetRequest         Game0031PlayerSideBet       = 3112; // [9KG-0031]閒家押注邊注請求
        pb.client.game.Game0031SeaterActionRequest          Game0031SeaterAction        = 3113; // [9KG-0031]入座玩家動作請求
    }
}

// [客戶端]回應
message ClientResponse {
    pb.base.ErrResp                                         ErrResp                     = 1 ;   // 錯誤回應

    // (以下欄位ErrResp無錯誤才會取得)
    oneof Response {
        pb.client.login.LoginAuthResponse                   LoginAuth                   = 11 ;  // 登入驗證回應
        pb.client.login.ReLoginAuthResponse                 ReLoginAuth                 = 12 ;  // 重登驗證回應
        pb.client.login.GetCurrGameInfoResponse             GetCurrGameInfo             = 13 ;  // 取得[當前遊戲資訊]回應

        pb.client.lobby.EnterLobbyResponse                  EnterLobby                  = 21 ;  // 入廳回應
        pb.client.lobby.ChangeLobbyResponse                 ChangeLobby                 = 22 ;  // 換廳回應
        pb.client.lobby.ChangeTableResponse                 ChangeTable                 = 23 ;  // 換桌回應
        pb.client.lobby.EnterSeatResponse                   EnterSeat                   = 24 ;  // 入座回應
        pb.client.lobby.LeaveSeatResponse                   LeaveSeat                   = 25 ;  // 離座回應

        pb.client.lobby.SeaterSendEmojiResponse             SeaterSendEmoji             = 31 ;  // 入座玩家發送表情回應

        pb.client.game.Game0031SeaterSendChatMsgResponse    Game0031SeaterSendChatMsg   = 3101; // [9KG-0031]入座玩家發送聊天訊息回應
        pb.client.game.Game0031PlayerBetResponse            Game0031PlayerBet           = 3111; // [9KG-0031]閒家押注回應
        pb.client.game.Game0031PlayerSideBetResponse        Game0031PlayerSideBet       = 3112; // [9KG-0031]閒家押注邊注回應
        pb.client.game.Game0031SeaterActionResponse         Game0031SeaterAction        = 3113; // [9KG-0031]入座玩家動作回應
    }
}

// [客戶端]通知
message ClientNotify {
    oneof Notify {
        pb.client.lobby.CurrTableInfoNotify                 CurrTableInfo                   = 90011 ;   // [用戶入桌,用戶入座,用戶離座,用戶斷線重連,他人入座,他人離座]時發送:[當前桌資訊通知]
        pb.client.lobby.SeaterEmojiNotify                   SeaterEmoji                     = 90012 ;   // [入座玩家發送表情請求]時發送:[入座玩家表情通知]
        pb.client.lobby.SystemKickTableUserNotify           SystemKickTableUser             = 90013 ;   // [桌用戶被系統踢出廳桌或者座位時]時發送:[系統強踢桌用戶通知]

        pb.client.game.Game0031WaitRoundNotify              Game0031WaitRound               = 100311;   // [9KG-0031][遊戲局不存在]時[每秒]發送:[等待開局通知]
        pb.client.game.Game0031RoundStatusForGuesterNotify  Game0031RoundStatusForGuester   = 100312;   // [9KG-0031][遊戲局階段變更]時發送:[遊戲局狀態遊客通知]
        pb.client.game.Game0031RoundStatusForGamerNotify    Game0031RoundStatusForGamer     = 100313;   // [9KG-0031][遊戲局存在]時[每秒]發送:[遊戲局狀態玩家通知]
    }
}
