syntax = "proto3";
package pb.client.login;
option go_package = "server-9kg/server/pb/client/login";

// -----------------------------------------------
// 登入驗證狀態
enum EnumLoginAuthStatus {
    E_LOGIN_AUTH_LOGIN_SUCCESSED    = 0 ;   // [狀態]登入成功
    E_LOGIN_AUTH_RELOGIN_SUCCESSED  = 1 ;   // [狀態]重登成功(斷線重連)
    E_LOGIN_AUTH_FAILED             = 2 ;   // [狀態]驗證失敗
    E_LOGIN_AUTH_MULTI_LOGIN_FAILED = 3 ;   // [狀態]多重登入失敗
    E_LOGIN_AUTH_ID_INVALID         = 4 ;   // [狀態]帳號無效
    E_LOGIN_AUTH_TOKEN_EXPIRED      = 5 ;   // [狀態]驗證碼已失效(請重新登入)
    E_LOGIN_AUTH_MAX                = 6 ;   // [狀態]上限判定用
}

// 登入驗證請求
message LoginAuthRequest {
    string                  HostID          = 1 ;   // 合作方帳號(完整登入用)
    string                  HostToken       = 2 ;   // 合作方驗證碼(完整登入用)
}
// 登入驗證回應
message LoginAuthResponse {
    EnumLoginAuthStatus     Status          = 1 ;   // 登入驗證狀態(EnumLoginAuthStatus)
    // (以下欄位Status成功才會取得)
    string                  UserID          = 11;   // 用戶帳號
    string                  GameToken       = 12;   // 遊戲驗證碼
    uint64                  UserSN          = 13;   // 用戶序號(Server/Client遊戲中通訊用)
    string                  UserName        = 14;   // 用戶名稱(玩家實際看到的遊戲中名字)
    string                  Balance         = 15;   // 用戶餘額(有小數點, 小數有效位數:2位:0.00)
}

// -----------------------------------------------
// 重登驗證請求
message ReLoginAuthRequest {
    string                  UserID          = 1 ;   // 用戶帳號
    string                  GameToken       = 2 ;   // 遊戲驗證碼(重登驗證用)
}
// 重登驗證回應
message ReLoginAuthResponse {
    EnumLoginAuthStatus     Status          = 1 ;   // 登入驗證狀態(EnumLoginAuthStatus)
    // (以下欄位Status成功才會取得)
    string                  UserID          = 11;   // 用戶帳號
    uint64                  UserSN          = 12;   // 用戶序號(Server/Client遊戲中通訊用)
    string                  UserName        = 13;   // 用戶名稱(玩家實際看到的遊戲中名字)
    string                  Balance         = 14;   // 用戶餘額(有小數點, 小數有效位數:2位:0.00)
}

// -----------------------------------------------
// 取得[當前遊戲資訊]請求
message GetCurrGameInfoRequest {
    string                  GameToken               = 1 ;   // 遊戲驗證碼
}
// 取得[當前遊戲資訊]回應
message GetCurrGameInfoResponse {
    string                  GameID                  = 1 ;   // 遊戲名稱

    uint32                  StaticLobbyMax          = 11 ;  // [靜態表]廳種上限
    string                  StaticLobbyName         = 12 ;  // [靜態表]廳種名稱(JSON): map{ key:"Language", value: LobbyName[] }
    map<uint32, LobbyInfo>  StaticLobbyInfo         = 13 ;  // [靜態表]廳種資訊表: map{ key: LobbyNo(1~StaticLobbyMax), value: LobbyInfo }
}

// 廳種資訊
message LobbyInfo {
    string                  StaticMinBet            = 11;   // [靜態表]最小押注(0:無限制,有值:押注下限)
    string                  StaticMaxBet            = 12;   // [靜態表]最大押注(0:無限制,有值:押注上限)
    string                  StaticPlayerMinChips    = 13;   // [靜態表]玩家最低籌碼進入門檻
    repeated string         StaticBetChips          = 14;   // [靜態表]押注籌碼列表
}
