syntax = "proto3";
package pb.client.game;
option go_package = "server-9kg/server/pb/client/game";

// -----------------------------------------------
// [0031]遊戲局階段
enum EnumGame0031RoundState {
    E_GAME0031_ROUND_STATE_NONE             = 0 ;   // [遊戲局階段]:無
    E_GAME0031_ROUND_STATE_ON_DEALER        = 1 ;   // [遊戲局階段]:選莊階段
    E_GAME0031_ROUND_STATE_ON_BET           = 2 ;   // [遊戲局階段]:下注階段
    E_GAME0031_ROUND_STATE_ON_DRAW          = 3 ;   // [遊戲局階段]:發牌階段
    E_GAME0031_ROUND_STATE_ON_PLAY          = 4 ;   // [遊戲局階段]:操作階段
    E_GAME0031_ROUND_STATE_ON_VS_RESULT     = 5 ;   // [遊戲局階段]:VS結算階段
    E_GAME0031_ROUND_STATE_ON_DEALER_PLAY   = 6 ;   // [遊戲局階段]:莊家操作階段
    E_GAME0031_ROUND_STATE_ON_RESULT        = 7 ;   // [遊戲局階段]:結算階段
    E_GAME0031_ROUND_STATE_ON_BREAK_OFF     = 8 ;   // [遊戲局階段]:中止階段(提前關局)
}

// [0031]牌型
enum EnumGame0031CardKind {
    E_GAME0031_CARD_KIND_NONE               = 0 ;   // [牌型]散牌(無,賠率x1)
    E_GAME0031_CARD_KIND_2PAIR              = 1 ;   // [牌型]對子(兩張牌點數一樣,賠率x2)
    E_GAME0031_CARD_KIND_2FLUSH             = 2 ;   // [牌型]二花(兩張牌花色一樣,賠率x2)
    E_GAME0031_CARD_KIND_3FLUSH             = 3 ;   // [牌型]三花(三張牌花色一樣,賠率x3)
    E_GAME0031_CARD_KIND_3KIND              = 4 ;   // [牌型]三條(三張牌點數一樣,賠率x5)
    E_GAME0031_CARD_KIND_AUTO_8             = 5 ;   // [牌型]天牌8(兩張牌總點數為8,賠率x1)
    E_GAME0031_CARD_KIND_AUTO_9             = 6 ;   // [牌型]天牌9(兩張牌總點數為9,賠率x1)
    E_GAME0031_CARD_KIND_AUTO_PAIR_8        = 7 ;   // [牌型]天對8(兩張牌總點數為8且點數一樣,賠率x2)
    E_GAME0031_CARD_KIND_AUTO_FLUSH_8       = 8 ;   // [牌型]天花8(兩張牌總點數為8且花色一樣,賠率x2)
    E_GAME0031_CARD_KIND_AUTO_FLUSH_9       = 9 ;   // [牌型]天花9(兩張牌總點數為9且花色一樣,賠率x2)
}

// -----------------------------------------------
// [0031]發送聊天狀態
enum EnumGame0031SendChatStatus {
    E_GAME0031_SEND_CHAT_SUCCESSED          = 0 ;   // [狀態]發送成功
    E_GAME0031_SEND_CHAT_FAILED             = 1 ;   // [狀態]發送失敗
    E_GAME0031_SEND_CHAT_MSG_OVER_LIMIT     = 2 ;   // [狀態]發送訊息過長(字數上限:50個字元)
    E_GAME0031_SEND_CHAT_MSG_TOO_FAST       = 3 ;   // [狀態]發送訊息過快(1秒只允許發1條訊息)
    E_GAME0031_SEND_CHAT_MAX                = 4 ;   // [狀態]上限判定用
}

// [0031]入座玩家發送聊天訊息請求
message Game0031SeaterSendChatMsgRequest {
    string                      GameToken       = 1 ;   // 遊戲驗證碼

    string                      SendChatMsg     = 11;   // 發送聊天訊息(字數上限:50個字元)
}
// [0031]入座玩家發送聊天訊息回應
message Game0031SeaterSendChatMsgResponse {
    EnumGame0031SendChatStatus  Status          = 1 ;   // 發送聊天狀態(EnumSendChatStatus)
    // (以下欄位Status成功才會取得)
    uint32                      SeatNo          = 11;   // 遊戲座號
    Game0031ChatMsg             ChatMsg         = 12;   // 聊天訊息
}

// -----------------------------------------------
// [0031]押注狀態
enum EnumGame0031BetStatus {
    E_GAME0031_BET_SUCCESSED                = 0 ;   // [狀態]押注成功
    E_GAME0031_BET_FAILED                   = 1 ;   // [狀態]押注失敗
    E_GAME0031_BET_BALANCE_NOT_ENOUGH       = 2 ;   // [狀態]押注餘額不足
    E_GAME0031_BET_MAX                      = 3 ;   // [狀態]上限判定用
}

// [0031]閒家押注請求
message Game0031PlayerBetRequest {
    string                      GameToken       = 1 ;   // 遊戲驗證碼

    string                      BetScore        = 11;   // 下注分數
}
// [0031]閒家押注回應
message Game0031PlayerBetResponse {
    EnumGame0031BetStatus       Status          = 1 ;   // 押注狀態(EnumBetStatus)
    // (以下欄位Status成功才會取得)
    uint32                      SeatNo          = 14;   // 遊戲座號

    string                      PlayerBalance   = 21;   // 閒家餘額(Balance+InsureFunds)
    string                      PlayerBetScore  = 22;   // 閒家注額
}

// -----------------------------------------------
// [0031]押注邊注狀態
enum EnumGame0031SideBetStatus {
    E_GAME0031_SIDEBET_SUCCESSED            = 0 ;   // [狀態]押注邊注成功
    E_GAME0031_SIDEBET_FAILED               = 1 ;   // [狀態]押注邊注失敗
    E_GAME0031_SIDEBET_BALANCE_NOT_ENOUGH   = 2 ;   // [狀態]押注邊注餘額不足
    E_GAME0031_SIDEBET_NO_BET               = 3 ;   // [狀態]閒家尚未下注
    E_GAME0031_SIDEBET_MAX                  = 4 ;   // [狀態]上限判定用
}

// [0031]閒家押注邊注請求
message Game0031PlayerSideBetRequest {
    string                      GameToken       = 1 ;   // 遊戲驗證碼

    uint32                      SideBetSeatNo   = 11;   // 下注邊注座號
}
// [0031]閒家押注邊注回應
message Game0031PlayerSideBetResponse {
    EnumGame0031SideBetStatus   Status          = 1 ;   // 押注邊注狀態(EnumSideBetStatus)
    // (以下欄位Status成功才會取得)
    uint32                      SeatNo          = 14;   // 遊戲座號

    string                      PlayerBalance   = 21;   // 閒家餘額(Balance+InsureFunds)

    uint32                      SideBetSeatNo   = 31;   // 邊注座號
    Game0031SideBet             SideBetStatus   = 32;   // 邊注狀態
}

// -----------------------------------------------
// [0031]入座玩家動作狀態
enum EnumGame0031SeaterActionStatus {
    E_GAME0031_SEATER_ACTION_SUCCESSED      = 0 ;   // [狀態]玩家動作成功
    E_GAME0031_SEATER_ACTION_FAILED         = 1 ;   // [狀態]玩家動作失敗
    E_GAME0031_SEATER_ACTION_IS_SET         = 2 ;   // [狀態]玩家動作已設定
    E_GAME0031_SEATER_ACTION_MAX            = 3 ;   // [狀態]上限判定用
}

// [0031]入座玩家動作請求
message Game0031SeaterActionRequest {
    string                          GameToken       = 1 ;   // 遊戲驗證碼

    string                          Action          = 11;   // 動作:["Versus","Hit","Stand","Finish"], ("Versus":僅莊家可用, "Hit":立即補第3張牌, "Finish":莊家天牌時用)
}
// [0031]入座玩家動作回應
message Game0031SeaterActionResponse {
    EnumGame0031SeaterActionStatus  Status          = 1 ;   // 入座玩家動作狀態(EnumSeaterActionStatus}
    // (以下欄位Status成功才會取得)
    uint32                          SeatNo          = 14;   // 遊戲座號

    string                          SeaterAction    = 21;   // 入座玩家動作狀態
    Game0031HandCard                SeaterHandCard  = 22;   // 入座玩家手牌
}

// -----------------------------------------------
// [0031][遊戲局不存在]時[每秒]發送:[等待開局通知]
message Game0031WaitRoundNotify {
	uint32                          RemainSec           = 1 ;   // 等待開局剩餘秒數(倒數至0秒時: 開局失敗, 將所有入座用戶踢回遊客區)
}

// -----------------------------------------------
// [0031][遊戲局階段變更]時發送:[遊戲局狀態遊客通知]
message Game0031RoundStatusForGuesterNotify {
    string                          RoundID             = 1 ;   // 遊戲局號
    uint32                          RoundNo             = 2 ;   // 遊戲局回合數
    EnumGame0031RoundState          RoundState          = 3 ;   // 遊戲局階段

    string                          DealerUserID        = 11;   // 莊家用戶編號
    string                          DealerBalance       = 12;   // 莊家餘額(Score)
    string                          DealerBetPool       = 13;   // 莊家下注獎池(Score)

    string                          PlayerTotalBetScore = 21;   // 閒家總下注金額(Score)
}

// -----------------------------------------------
// [0031][遊戲局存在]時[每秒]發送:[遊戲局狀態玩家通知]
message Game0031RoundStatusForGamerNotify {
    string                          RoundID             = 1 ;   // 遊戲局號
    uint32                          RoundNo             = 2 ;   // 遊戲局回合數
    EnumGame0031RoundState          RoundState          = 3 ;   // 遊戲局階段
    uint32                          PrepareRemainSec    = 4 ;   // 準備剩餘秒數
    uint32                          StateRemainSec      = 5 ;   // 階段剩餘秒數
    uint32                          StateActionSeatNo   = 6 ;   // 階段動作座號(有值:表示當前輪到哪個座位動作)
    bool                            IsAutoCard          = 7 ;   // 是否有[莊家]或[所有閒家]為天牌

    map<uint32, string>             SeatBalance         = 11;   // 座位餘額: map[座號(SeatNo), 餘額(Balance+InsureFunds)]
    map<uint32, Game0031HandCard>   SeatHandCard        = 12;   // 座位手牌: map[座號(SeatNo), 手牌(Game0031HandCard)]
    map<uint32, string>             SeatAction          = 13;   // 座位動作: map[座號(SeatNo), 動作:["Versus","Hit","Stand","Finish"]]

    uint32                          DealerSeatNo        = 21;   // 莊家座號
    string                          DealerUserID        = 22;   // 莊家用戶編號
    string                          DealerBetPool       = 23;   // 莊家下注獎池(Score)

    map<uint32, string>             PlayerBetArea       = 31;   // 閒家下注區: map[座號(SeatNo), 注額(BetScore)]
    map<uint32, Game0031SideBet>    PlayerSideBetArea   = 32;   // 閒家邊注區: map[座號(SeatNo), 邊注(Game0031SideBet)]
    string                          PlayerTotalBetScore = 33;   // 閒家總下注金額(Score)

    map<uint32, Game0031PayResultList>  SeatPayResult   = 41;   // 座位賠付區: map[座號(SeatNo), 賠付結算列表(Game0031PayResultList)]

    repeated Game0031ChatMsg        ChatRoom            = 51;   // 聊天室(僅遊戲局內存在, 入座玩家可見)
}

// [0031]手牌
message Game0031HandCard {
    repeated string                 Cards               = 1 ;   // 手牌[2~3]
    uint32                          Point               = 2 ;   // 總點數
    EnumGame0031CardKind            Kind                = 3 ;   // 牌型
    uint32                          Odds                = 4 ;   // 賠率[1,2,3,5]
}

// [0031]邊注
message Game0031SideBet {
    uint32                          FromSeatNo          = 1 ;   // 下注者座號
    string                          BetScore            = 2 ;   // 注額
}

// [0031]賠付結算
message Game0031PayResult {
    uint32                          BetSeatNo           = 1 ;   // 下注者座號
    uint32                          SideBetSeatNo       = 2 ;   // 邊注者座號(若存在則表示此為邊注單)
    bool                            BetIsDealer         = 3 ;   // 下注者為莊家
    string                          BetScore            = 5 ;   // 下注分數
    bool                            IsWin               = 6 ;   // 是否下注者獲勝(下注者為莊家時忽略此欄位)
    uint32                          PaySeqNo            = 11;   // 賠付順位
    string                          Payout              = 12;   // 贏分(BetScore * (1 + Odds))
//  string                          WinLose             = 13;   // 輸贏(Payout - BetScore)
//  string                          Fee                 = 14;   // 系統抽水分數(WinLose[+] * Rate)
    string                          RealPayout          = 15;   // 實際贏分(Payout - Fee)
    string                          RealWinLose         = 16;   // 實際輸贏(WinLose - Fee)
}

// [0031]賠付結算列表
message Game0031PayResultList {
    repeated Game0031PayResult      List                = 1 ;   // 列表
}

// [0031]聊天訊息
message Game0031ChatMsg {
    string                          Sender              = 1 ;   // 發送者["System":系統, Others:用戶編號(UserID)]
    int64                           SendTime            = 2 ;   // 發送時間戳(UnixTime, UTC+0)
    string                          SendMsg             = 3 ;   // 發送訊息
}
